package com.looping;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		switch(100)
		{
		case 100: System.out.println("hiii");
		break;
		case 200: System.out.println("welcome");
		break;
		case 300: System.out.println("bye");
		break;
		default: System.out.println("goodbye");
		}
	}

}
